import pandas as pd

df = pd.DataFrame(
    {
        "Name": ["Jim", "Lily", "Jack"],
        "Statistics": [82, 68, 82],
        "Accounting": [72, 63, 72],
        "Marketing": [84, 90, 78],
    }
)

df

df.groupby("Level").mean(
    numeric_only=True
)  # Get the mean scores for each module by "Level"

def replace_score(x):
    if x >= 70:
        return "A"
    elif x >= 60:
        return "B"
    elif x >= 50:
        return "C"
    else:
        return "Failed"


df["new Statistics"] = df["new Statistics"].apply(replace_score)
df
